    private PIDController hatchLifter_PID;

    // END AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=DECLARATIONS

    public HatchLifter() {
        // BEGIN AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=CONSTRUCTORS
        lifterMotor = new Spark(2);
        addChild("lifterMotor",lifterMotor);
        lifterMotor.setInverted(false);
        
        encoder = new Encoder(1, 2, true, EncodingType.k4X);
        addChild("encoder",encoder);
        encoder.setDistancePerPulse(1.0);
        encoder.setPIDSourceType(PIDSourceType.kRate);
        
        hatchLifter_PID = new PIDController(0.001, 0.0, 0.0, 0.0, encoder, lifterMotor, 0.5);
        addChild("hatchLifter_PID",hatchLifter_PID);
        hatchLifter_PID.setContinuous(false);
        hatchLifter_PID.setAbsoluteTolerance(0.2);

        hatchLifter_PID.setOutputRange(-1.0, 1.0);
        

    // END AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=CONSTRUCTORS
        
        init();
    }

    public String activeSide = "Hatch";    // Variable that stores whether the hatch or the cargo side is in use
                                            // Currently placed in this subsystem b/c it doesn't work in the main Robot class

    double endTime = 0;
    double P = 0.009;
    double I = 0.0;
    double D = 0.0;

    // Motor specifications
    final int pulsesPerRevolution = 7;                                          // The amount of pulses received after one full revolution of the motor.
    final double gearRatio = 71;                                               // The gear ratio for the lifter motor
    final double degreePerPulse = 360 / (gearRatio * pulsesPerRevolution);      // Finds how many degrees each pulse represents
    
    final double armHeight = 33.7;                                              // How hight up the pivot point of the arm is from the ground
    final double armLength = 17.0;                                              // The length of the arm

    boolean usingIntervals = false;                 // Whether the robot is moving based on intervals or the joystick throttle
    int intervalGoal = 0;                           // The current height interval that the robot is aiming for
    double[] totalGoals;                   // All the intervals the robot can switch between
    final double hatchTolerance = 5;                // How far off the encoder counts can be from the goal

    // Initialization method for the subsystem
    public void init(){
        totalGoals = new double[]{260,1010};
    }

    // *** Main method for the subsystem that should be called by the command *** //
    public void moveHatchLifter(){
        if(usingIntervals){
            moveToInterval(totalGoals[intervalGoal]);
        }
        else{
            moveManually();
            SmartDashboard.putNumber("Hatch Lift Sped: ", lifterMotor.get());
        }

        SmartDashboard.putNumber("Hatch Encoder: ", encoder.get());

    }

    // **************************** Interval Code *********************************** //

    // Changes what height the hatch aims for
    public void changeHatchInterval(){
        // Moves up an interval
        intervalGoal = ++intervalGoal % totalGoals.length;

        System.out.println("Current lift mode is: " + intervalGoal);
    }

    // Start up code for my custom pid loop for interval control
    public void startIntervals(){
        endTime = Timer.getFPGATimestamp();
    }

    // Moves the hatch lifter to preset heights
    // Uses a custom PID controller
    public void moveToInterval(double goal){                
        double startTime = Timer.getFPGATimestamp();

        // Converts the amount of rotations you want to the amount of encoder counts you should get
        // double goal = totalGoals[intervalGoal] * pulsesPerRevolution * gearRatio;


        double error = goal - encoder.get();
        double deltaTime = startTime - endTime;

        System.out.println("Erorr is: " + error);

        if(Math.abs(error) >= hatchTolerance){
            double speed = error * P + (error*deltaTime) * I + (error/deltaTime) * D;

            lifterMotor.set(speed);
        }
        // Stops the motor if it has reached its goal
        else{
            System.out.println("At goal of " + totalGoals[intervalGoal] + " counts");
            lifterMotor.stopMotor();
        }

        // endTime = startTime;     
    }

    // Moves the hatch lifter to a preset height
    // Based on the WPI PID controller
    public void WPI_PIDIntervals(){
        // Enables the PID controller if it isn't already
        if(!hatchLifter_PID.isEnabled()){
            hatchLifter_PID.enable();
        }

        // Moves the hatch lifter to its appropriate goal
        switch (intervalGoal){
            case 0:
                moveToFirst();
                break;
            case 1:
                moveToSecond();
                break;
            default:
                moveToFirst();
        }
    }

    // Method for moving the lift to the height of the first hatch level
    public void moveToFirst(){
        hatchLifter_PID.setSetpoint(150);
    }

    // Method for moving the lift to the height of the the second hatch level
    public void moveToSecond(){
        hatchLifter_PID.setSetpoint(-100);
    }
    
